REPLACE INTO `ace_object_properties_book` (`aceObjectId`, `page`, `authorName`, `authorAccount`, `authorId`, `ignoreAuthor`, `pageText`) VALUES ('8761', '0', '', 'prewritten', '4294967295', '0', 'Shoushi 33.5 S 72.8 E\nSoutheast Shoushi Outpost 36.7 S 75.8 E\nWest Shoushi Outpost 33.6 S 69.8 E\nPortal to Nanto 32.3 S 71.7 E\nPortal to Yanshi 34.9 S 74.4 E\nLocation of Shoushi Town Stamp: Shreth Hive Portal 32.5 S 71 E');
