REPLACE INTO `ace_object_properties_book` (`aceObjectId`, `page`, `authorName`, `authorAccount`, `authorId`, `ignoreAuthor`, `pageText`) VALUES ('25728', '0', 'Gentleman Jake Hawkins', 'prewritten', '4294967295', '0', 'To whom it may concern,\nI heard rumors of a plot to remove an important woman in power. I became frightened and left the bandit stronghold where I was living and went on the run. I feel terrible about what happened to High Queen Elysa. I fear that the men who are in charge of the bandits may be behind the attempt on her life.\nGentleman Jake Hawkins');
